'use strict';

// MODULES //

var constructorName = require( '@stdlib/utils/constructor-name' );
var itypes = require( './itypes.js' );


// MAIN //

/**
* Returns a value's corresponding data type.
*
* @private
* @param {*} value - input value
* @returns {(string|null)} data type
*
* @example
* var t = dtype( [ 1, 2, 3 ] );
* // returns 'generic'
*/
function dtype( value ) {
	var t = constructorName( value );
	t = itypes[ t ] || null;
	return t;
} // end FUNCTION dtype()


// EXPORTS //

module.exports = dtype;
