'use strict';

// MAIN //

/**
* Fills a generic array with zeros.
*
* @private
* @param {Array} arr - input array
* @returns {Array} input array
*
* @example
* var arr = zero( new Array( 2 ) );
* // returns [ 0, 0 ]
*/
function zeros( arr ) {
	var i;
	for ( i = 0; i < arr.length; i++ ) {
		arr[ i ] = 0.0;
	}
	return arr;
} // end FUNCTION zeros()


// EXPORTS //

module.exports = zeros;
