'use strict';

var sub2ind = require( './../lib' );

var shape = [ 3, 3, 3 ];
var len = shape[ 0 ] * shape[ 1 ] * shape[ 2 ];

var arr = new Array( len );
var i;
for ( i = 0; i < len; i++ ) {
	arr[ i ] = i;
}

var opts = {
	'order': 'column-major'
};

console.log( '' );
console.log( 'Dimensions: %s.', shape.join( 'x' ) );
console.log( 'View:' );
console.log( '' );

var slice;
var idx;
var row;
var j;
var k;
for ( k = 0; k < shape[ 2 ]; k++ ) {
	slice = 'A[:,:,'+k+'] = ';
	console.log( slice );
	for ( i = 0; i < shape[ 0 ]; i++ ) {
		row = '  ';
		for ( j = 0; j < shape[ 1 ]; j++ ) {
			idx = sub2ind( shape, i, j, k, opts );
			row += arr[ idx ];
			if ( j < shape[ 1 ]-1 ) {
				row += ', ';
			}
		}
		console.log( row );
	}
	console.log( '' );
}
