'use strict';

// MODULES //

var tape = require( 'tape' );
var instanceOf = require( '@stdlib/assert/instance-of' );
var ndarray = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ndarray, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function is a constructor', function test( t ) {
	var opts;
	var out;

	opts = {
		'shape': [ 2, 3 ]
	};
	out = new ndarray( opts );

	t.strictEqual( instanceOf( out, ndarray ), true, 'returns an instance' );
	t.end();
});

tape( 'the constructor does not require the `new` keyword', function test( t ) {
	var opts;
	var out;

	opts = {
		'shape': [ 2, 3 ]
	};
	out = ndarray( opts );

	t.strictEqual( instanceOf( out, ndarray ), true, 'returns an instance' );
	t.end();
});

tape( 'the constructor throws an error if provided an options argument which is not an object', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			ndarray( value );
		};
	}
});

tape( 'the constructor throws an error if not provided a shape', function test( t ) {
	t.throws( badValue( {} ), TypeError, 'throws an error when not provided a shape' );
	t.end();

	function badValue( value ) {
		return function badValue() {
			ndarray( value );
		};
	}
});

tape( 'the constructor throws an error if provided an invalid option', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		'beep',
		'boop',
		'foo',
		'bar',
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			var opts = {
				'shape': [ 3, 2 ],
				'dtype': value
			};
			ndarray( opts );
		};
	}
});

tape( 'the constructor throws an error if provided an `offset` option which exceeds a provided buffer length', function test( t ) {
	var opts = {
		'offset': 100,
		'shape': [ 3, 3 ],
		'buffer': [ 1, 2, 3, 4, 5, 6 ]
	};
	t.throws( badValue( opts ), RangeError, 'throws a range error when provided an invalid `offset` option' );
	t.end();

	function badValue( value ) {
		return function badValue() {
			ndarray( value );
		};
	}
});

