'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pow = require( '@stdlib/math/base/special/pow' );
var toFloat32 = require( '@stdlib/types/number/float64/base/to-float32' );
var toBinaryStringf = require( '@stdlib/types/number/float32/base/to-binary-string' );
var fromBinaryStringf = require( './../lib' );

var frac;
var sign;
var exp;
var b;
var x;
var y;
var i;

// Convert random numbers to IEEE 754 literal bit representations and then convert them back...
for ( i = 0; i < 100; i++ ) {
	if ( randu() < 0.5 ) {
		sign = -1.0;
	} else {
		sign = 1.0;
	}
	frac = randu() * 10.0;
	exp = round( randu()*100.0 );
	if ( randu() < 0.5 ) {
		exp = -exp;
	}
	x = sign * frac * pow( 2.0, exp );
	x = toFloat32( x );

	b = toBinaryStringf( x );
	y = fromBinaryStringf( b );

	console.log( '%d => %s => %d', x, b, y );
	console.log( x === y );
}
