'use strict';

/**
* Typed array constructor which returns a typed array representing an array of single-precision floating-point numbers in the platform byte order.
*
* @module @stdlib/types/typed-array/float32array
*
* @example
* var ctor = require( '@stdlib/types/typed-array/float32array' );
*
* var arr = new ctor( 10 );
* // returns <Float32Array>
*/

// MODULES //

var hasFloat32ArraySupport = require( '@stdlib/utils/detect-float32array-support' );


// MAIN //

var ctor;
if ( hasFloat32ArraySupport() ) {
	ctor = require( './float32array.js' );
} else {
	ctor = require( './polyfill.js' );
}


// EXPORTS //

module.exports = ctor;
