'use strict';

/**
* Typed array constructor which returns a typed array representing an array of twos-complement 8-bit signed integers in the platform byte order.
*
* @module @stdlib/types/typed-array/int8array
*
* @example
* var ctor = require( '@stdlib/types/typed-array/int8array' );
*
* var arr = new ctor( 10 );
* // returns <Int8Array>
*/

// MODULES //

var hasInt8ArraySupport = require( '@stdlib/utils/detect-int8array-support' );


// MAIN //

var ctor;
if ( hasInt8ArraySupport() ) {
	ctor = require( './int8array.js' );
} else {
	ctor = require( './polyfill.js' );
}


// EXPORTS //

module.exports = ctor;
