'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var hasInt8ArraySupport = require( '@stdlib/utils/detect-int8array-support' )();
var polyfill = require( './../lib/polyfill.js' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'if an environment supports `Int8Array`, the export is an alias for `Int8Array`', function test( t ) {
	var Foo;

	Foo = proxyquire( './../lib', {
		'@stdlib/utils/detect-int8array-support': isTrue,
		'./int8array.js': Mock
	});
	t.strictEqual( Foo, Mock, 'returns builtin' );

	if ( hasInt8ArraySupport ) {
		t.strictEqual( ctor, Int8Array, 'is alias' );
	}

	t.end();

	function Mock() {
		return this;
	}

	function isTrue() {
		return true;
	}
});

tape( 'if an environment does not support `Int8Array`, the export is a polyfill', function test( t ) {
	var Foo;

	Foo = proxyquire( './../lib', {
		'@stdlib/utils/detect-int8array-support': isFalse
	});

	t.strictEqual( Foo, polyfill, 'returns polyfill' );
	t.end();

	function isFalse() {
		return false;
	}
});
