'use strict';

/**
* Revive a JSON-serialized typed array.
*
* @module @stdlib/types/typed-array/utils/reviver
*
* @example
* var parseJSON = require( '@stdlib/utils/parse-json' );
* var reviver = require( '@stdlib/types/typed-array/utils/reviver' );
*
* var str = '{"type":"Float64Array","data":[5,3]}';
*
* var arr = parseJSON( str, reviver );
* // returns <Float64Array>[ 5.0, 3.0 ]
*/

// MODULES //

var reviver = require( './reviver.js' );


// EXPORTS //

module.exports = reviver;
