'use strict';

/**
* Return the minimum safe integer capable of being represented by a numeric real type.
*
* @module @stdlib/types/utils/safeintmin
*
* @example
* var safeintmin = require( '@stdlib/types/utils/safeintmin' );
*
* var m = safeintmin( 'float64' );
* // returns -9007199254740991
*
* m = safeintmin( 'float16' );
* // returns -2047
*
* m = safeintmin( 'float32' );
* // returns -16777215
*/

// MODULES //

var safeintmin = require( './safeintmin.js' );


// EXPORTS //

module.exports = safeintmin;
