'use strict';

/**
* Return the maximum value of a specified numeric type.
*
* @module @stdlib/types/utils/type-max
*
* @example
* var typemax = require( '@stdlib/types/utils/type-max' );
*
* var m = typemax( 'int8' );
* // returns 127
*
* m = typemax( 'uint8' );
* // returns 255
*
* m = typemax( 'int16' );
* // returns 32767
*/

// MODULES //

var typemax = require( './typemax.js' );


// EXPORTS //

module.exports = typemax;
