'use strict';

var typemin = require( './../lib' );

var m = typemin( 'float64' );
console.log( m );
// => -infinity

m = typemin( 'float32' );
console.log( m );
// => -infinity

m = typemin( 'float16' );
console.log( m );
// => -infinity

m = typemin( 'int32' );
console.log( m );
// => -2147483648

m = typemin( 'uint32' );
console.log( m );
// => 0

m = typemin( 'int16' );
console.log( m );
// => -32768

m = typemin( 'uint16' );
console.log( m );
// => 0

m = typemin( 'int8' );
console.log( m );
// => -128

m = typemin( 'uint8' );
console.log( m );
// => 0

m = typemin( 'uint8c' );
console.log( m );
// => 0
