'use strict';

var randu = require( '@stdlib/random/base/randu' );
var anyBy = require( './../lib' );

function threshold( value ) {
	return ( value > 0.95 );
}

var bool;
var arr;
var i;

arr = new Array( 100 );
for ( i = 0; i < arr.length; i++ ) {
	arr[ i ] = randu();
}

bool = anyBy( arr, threshold );
console.log( bool );
