'use strict';

var Float64Array = require( '@stdlib/types/array/float64' );
var append = require( './../lib' );

var arr;
var i;

arr = new Float64Array();
for ( i = 0; i < 100; i++ ) {
	arr = append( arr, [ i, i+1, i+2 ] );
}
console.log( arr );
console.log( 'View Length: %d', arr.byteLength );
console.log( 'Buffer Length: %d', arr.buffer.byteLength );
