'use strict';

/**
* An array containing command-line arguments passed when launching the calling process.
*
* @module @stdlib/utils/argv
*
* @example
* var ARGV = require( '@stdlib/utils/argv' );
*
* console.log( ARGV );
* // => [...]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var ARGV;
if ( IS_BROWSER ) {
	ARGV = require( './browser.js' );
} else {
	ARGV = require( './argv.js' );
}


// EXPORTS //

module.exports = ARGV;
