'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var IS_BROWSER = require( '@stdlib/assert/is-browser' );
var isStringArray = require( '@stdlib/assert/is-string-array' ).primitives;
var ARGV = require( './../lib' );


// VARIABLES //

var opts = {
	'skip': IS_BROWSER
};


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ARGV, 'object', 'main export is an object' );
	t.end();
});

tape( 'in non-browser environments, the export is an alias for `process.argv`', function test( t ) {
	var ARGV = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': false
	});
	t.strictEqual( ARGV, process.argv, 'is an alias' );
	t.end();
});

tape( 'in non-browser environments, the export is an array of strings', opts, function test( t ) {
	t.strictEqual( isStringArray( ARGV ), true, 'is an array of strings' );
	t.end();
});

tape( 'in browser environments, the export is an empty array', function test( t ) {
	var ARGV = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': true
	});
	t.deepEqual( ARGV, [], 'is an empty array' );
	t.end();
});
