'use strict';

/**
* Split values into two groups according to a predicate function.
*
* @module @stdlib/utils/bifurcate-by-async
*
* @example
* var readFile = require( '@stdlib/fs/read-file' );
* var bifurcateByAsync = require( '@stdlib/utils/bifurcate-by-async' );
*
* var files = [
*     './beep.js',
*     './boop.js'
* ];
*
* function done( error, result ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( result );
* }
*
* function predicate( file, next ) {
*     var opts = {
*         'encoding': 'utf8'
*     };
*     readFile( file, opts, onFile );
*
*     function onFile( error ) {
*         if ( error ) {
*             return next( null, false );
*         }
*         next( null, true );
*     }
* }
*
* bifurcateByAsync( files, predicate, done );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var bifurcateByAsync = require( './bifurcate_by.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( bifurcateByAsync, 'factory', factory );


// EXPORTS //

module.exports = bifurcateByAsync;
