'use strict';

/**
* Function composition.
*
* @module @stdlib/utils/compose-async
*
* @example
* var composeAsync = require( '@stdlib/utils/compose-async' );
*
* function a( x, next ) {
*     setTimeout( onTimeout, 0 );
*     function onTimeout() {
*         next( null, 2*x );
*     }
* }
*
* function b( x, next ) {
*     setTimeout( onTimeout, 0 );
*     function onTimeout() {
*         next( null, x+3 );
*     }
* }
*
* function c( x, next ) {
*     setTimeout( onTimeout, 0 );
*     function onTimeout() {
*         next( null, x/5 );
*     }
* }
*
* var f = composeAsync( c, b, a );
*
* function done( error, result ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( result );
*     // => 3
* }
*
* f( 6, done );
*/

// MODULES //

var composeAsync = require( './compose_async.js' );


// EXPORTS //

module.exports = composeAsync;
