'use strict';

/**
* Function composition.
*
* @module @stdlib/utils/compose
*
* @example
* var compose = require( '@stdlib/utils/compose' );
*
* function a( x ) {
*     return 2 * x;
* }
*
* function b( x ) {
*     return x + 3;
* }
*
* function c( x ) {
*     return x / 5;
* }
*
* var f = compose( c, b, a );
*
* var z = f( 6 );
* // returns 3
*/

// MODULES //

var compose = require( './compose.js' );


// EXPORTS //

module.exports = compose;
