'use strict';

var constantFunction = require( './../lib' );

var fcn;
var arr;
var v;
var i;

fcn = constantFunction( 3.14 );
for ( i = 0; i < 10; i++ ) {
	v = fcn();
	console.log( v );
	// => 3.14
}

arr = [ 1, 2, 3 ];
fcn = constantFunction( arr );
for ( i = 0; i < 10; i++ ) {
	v = fcn();
	console.log( v === arr );
	// => true
}
