'use strict';

/**
* Test if a string is found in another string.
*
* @module @stdlib/utils/string/contains
*
* @example
* var contains = require( '@stdlib/utils/string/contains' );
*
* var out = contains( 'Hello World', 'World' );
* // returns true
*
* out = contains( 'Hello World', 'world' );
* // returns false
*
* out = contains( [ 1, 2, 3, 4 ], 2 );
* // returns true
*
* out = contains( [ NaN, 2, 3, 4 ], NaN );
* // returns true
*/

// MODULES //

var contains = require( './contains.js' );


// EXPORTS //

module.exports = contains;
