'use strict';

// TYPED ARRAY FUNCTIONS //

/*
* Create functions for copying typed arrays.
*/
var ctors = [
	'Int8Array',
	'Uint8Array',
	'Uint8ClampedArray',
	'Int16Array',
	'Uint16Array',
	'Int32Array',
	'Uint32Array',
	'Float32Array',
	'Float64Array'
];
var typedArrays = {};

/* jshint evil:true */ // TODO: eslint
var ctor;
var i;
for ( i = 0; i < ctors.length; i++ ) {
	ctor = ctors[ i ];
	typedArrays[ ctor.toLowerCase() ] = new Function( 'arr', 'return new '+ctor+'( arr );' );
}


// EXPORTS //

module.exports = typedArrays;
