'use strict';

var randu = require( '@stdlib/random/base/randu' );
var deepSet = require( './../lib' );

var data;
var bool;
var keys;
var i;

function set( val ) {
	return val * 10.0;
}

data = new Array( 100 );
for ( i = 0; i < data.length; i++ ) {
	data[ i ] = {
		'x': Date.now(),
		'y': [ randu(), randu(), i ]
	};
}

keys = [ 0, 'y', 2 ];
for ( i = 0; i < data.length; i++ ) {
	keys[ 0 ] = i;
	bool = deepSet( data, keys, set );
	if ( !bool ) {
		console.error( 'Unable to deep set value.' );
	}
}
console.log( data );
