tape( 'the function returns `false` if unable to successfully set', function test( t ) {
	var bool;
	var obj;

	obj = {
		'a': {
			'b': {
				'c': 'd'
			}
		},
		'arr': [
			{
				'x': 1,
				'y': 2
			},
			{
				'x': 3,
				'y': 4
			}
		],
		'null': null
	};

	bool = dset( obj, ['a','b','djfajdfaj'], false, 'beep' );
	t.equal( bool, false, 'returns false' );

	bool = dset( obj, ['null','e'], false, 'beep' );
	t.equal( bool, false, 'returns false' );
	t.end();
});

