tape( 'the function deep sets using a callback function', function test( t ) {
	var expected;
	var bool;
	var obj;

	obj = {
		'a': {
			'b': {
				'c': 'd'
			}
		},
		'arr': [
			{
				'x': 1,
				'y': 2
			},
			{
				'x': 3,
				'y': 4
			}
		],
		'null': null
	};

	expected = {
		'a': {
			'b': {
				'c': 'dub-beat'
			}
		},
		'arr': [
			{
				'x': 1,
				'y': 2
			},
			{
				'x': 3,
				'y': 4
			}
		],
		'null': null
	};

	bool = dset( obj, ['a','b','c'], false, set );
	t.deepEqual( obj, expected );
	t.equal( bool, true, 'returns true' );
	t.end();

	function set( val ) {
		return val + 'ub-beat';
	}
});
