'use strict';

var setReadOnly = require( './../lib' );

function Foo( name ) {
	if ( !(this instanceof Foo) ) {
		return new Foo( name );
	}
	setReadOnly( this, 'name', name );
	return this;
}

var foo = new Foo( 'beep' );

try {
	foo.name = 'boop';
} catch ( err ) {
	console.error( err.message );
}
