'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var detect = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof detect, 'function', 'main export is a function' );
	t.end();
});

tape( 'feature detection result is a boolean', function test( t ) {
	t.equal( typeof detect(), 'boolean', 'detection result is a boolean' );
	t.end();
});

tape( 'if `class` is supported, detection result is `true`', function test( t ) {
	var detect = proxyquire( './../lib/detect_class_support.js', {
		'@stdlib/utils/eval': stub
	});

	t.equal( detect(), true, 'detection result is `true`' );
	t.end();

	function stub() {
		return 'beep';
	}
});

tape( 'if `class` is not supported, detection result is `false`', function test( t ) {
	var detect = proxyquire( './../lib/detect_class_support.js', {
		'@stdlib/utils/eval': stub
	});

	t.equal( detect(), false, 'detection result is `false`' );
	t.end();

	function stub() {
		throw new Error( 'boop' );
	}
});
