'use strict';

// MODULES //

var foo = require( './foo.js' );


// IS FUNCTION NAME SUPPORTED //

/**
* Tests for native function `name` support.
*
* @returns {boolean} boolean indicating if an environment has function `name` support
*
* @example
* var bool = isFunctionNameSupported();
* // returns <Boolean>
*/
function isFunctionNameSupported() {
	return ( foo.name === 'foo' );
} // end FUNCTION isFunctionNameSupported()


// EXPORTS //

module.exports = isFunctionNameSupported;
