'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var detect = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof detect, 'function', 'main export is a function' );
	t.end();
});

tape( 'feature detection result is a boolean', function test( t ) {
	t.equal( typeof detect(), 'boolean', 'detection result is a boolean' );
	t.end();
});

tape( 'if functions have a `name` property, detection result is `true`', function test( t ) {
	var detect;
	var stub;

	stub = {};
	stub.name = 'foo';

	detect = proxyquire( './../lib', {
		'./foo.js': stub
	});

	t.equal( detect(), true, 'detection result is `true`' );
	t.end();
});

tape( 'if functions do not have a `name` property, detection result is `false`', function test( t ) {
	var detect;
	var stub;

	stub = {};
	stub.name = null;

	detect = proxyquire( './../lib', {
		'./foo.js': stub
	});

	t.equal( detect(), false, 'detection result is `false`' );
	t.end();
});
