'use strict';

// HAS SYMBOLS //

/**
* FUNCTION: areSymbolsSupported()
*	Tests for native `Symbol` support.
*
* @returns {Boolean} boolean indicating if an environment has `Symbol` support
*/
function areSymbolsSupported() {
	/* jshint newcap:false */
	return (
		typeof Symbol === 'function' &&
		typeof Symbol() === 'symbol'
	);
} // end FUNCTION areSymbolsSupported()


// EXPORTS //

module.exports = areSymbolsSupported;
