'use strict';

/**
* Invoke a function while a test condition is true.
*
* @module @stdlib/utils/do-while-async
*
* @example
* var doWhileAsync = require( '@stdlib/utils/do-while-async' );
*
* function fcn( i, next ) {
*     setTimeout( onTimeout, i );
*     function onTimeout() {
*         console.log( 'beep: %d', i );
*         next();
*     }
* }
*
* function predicate( i, clbk ) {
*     clbk( null, i < 5 );
* }
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* doWhileAsync( fcn, predicate, done );
*/

// MODULES //

var doWhileAsync = require( './do_while_async.js' );


// EXPORTS //

module.exports = doWhileAsync;
