'use strict';

var objectEntriesIn = require( './../lib' );

function Foo() {
	this.beep = 'boop';
	this.a = {
		'b': 'c'
	};
	return this;
}

Foo.prototype.foo = [ 'bar' ];

var obj = new Foo();
var entries = objectEntriesIn( obj );

console.log( entries );
// e.g., => [ ['beep', 'boop'], ['a', {'b':'c'}], ['foo', [ 'bar' ]] ]

