'use strict';

var err2json = require( '@stdlib/utils/error-to-json' );
var reviveError = require( './../lib' );

var err1 = new SyntaxError( 'bad syntax' );
// returns <SyntaxError>

var json = err2json( err1 );
/* returns
    {
        "type": "SyntaxError",
        "name": "SyntaxError",
        "message": "bad syntax",
        "stack": "<stack>"
    }
*/

var str = JSON.stringify( json );
// returns '{"type":"SyntaxError","name":"SyntaxError","message":"bad syntax","stack":"<stack>"}'

var err2 = JSON.parse( str, reviveError );
// returns <SyntaxError>

console.log( err1.message === err2.message );
// => true

console.log( err1.stack === err2.stack );
// => true

