'use strict';

var err2json = require( './../lib' );

var err = new Error( 'beep' );
console.log( err2json( err ) );
/* =>
	{
		"type": "Error",
		"name": "Error",
		"message": "beep",
		"stack": "..."
	}
*/

err = new TypeError( 'invalid type' );
console.log( err2json( err ) );
/* =>
	{
		"type": "TypeError",
		"name": "TypeError",
		"message": "invalid type",
		"stack": "..."
	}
*/

err = new SyntaxError( 'bad syntax' );
console.log( err2json( err ) );
/* =>
	{
		"type": "SyntaxError",
		"name": "SyntaxError",
		"message": "bad syntax",
		"stack": "..."
	}
*/

err = new ReferenceError( 'unknown variable' );
console.log( err2json( err ) );
/* =>
	{
		"type": "ReferenceError",
		"name": "ReferenceError",
		"message": "unknown variable",
		"stack": "..."
	}
*/

err = new URIError( 'bad URI' );
console.log( err2json( err ) );
/* =>
	{
		"type": "URIError",
		"name": "URIError",
		"message": "bad URI",
		"stack": "..."
	}
*/

err = new RangeError( 'value out-of-range' );
console.log( err2json( err ) );
/* =>
	{
		"type": "RangeError",
		"name": "RangeError",
		"message": "value out-of-range",
		"stack": "..."
	}
*/

err = new EvalError( 'eval error' );
console.log( err2json( err ) );
/* =>
	{
		"type": "EvalError",
		"name": "EvalError",
		"message": "eval error",
		"stack": "..."
	}
*/
