'use strict';

// MODULES //

var tape = require( 'tape' );
var evil = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof evil, 'function', 'main export is a function' );
	t.end();
});

tape( 'export is an alias for `eval`', function test( t ) {
	t.equal( evil, eval, 'alias for `eval`' );
	t.end();
});
