'use strict';

/**
* Test whether all elements in a collection pass a test implemented by a predicate function.
*
* @module @stdlib/utils/every-async
*
* @example
* var readFile = require( '@stdlib/fs/read-file' );
* var everyAsync = require( '@stdlib/utils/every-async' );
*
* var files = [
*     './beep.js',
*     './boop.js'
* ];
*
* function done( error, bool ) {
*     if ( error ) {
*         throw error;
*     }
*     if ( bool ) {
*         console.log( 'Successfully read all files.' );
*     } else {
*         console.log( 'Was unable to read all files.' );
*     }
* }
*
* function predicate( file, next ) {
*     var opts = {
*         'encoding': 'utf8'
*     };
*     readFile( file, opts, onFile );
*
*     function onFile( error ) {
*         if ( error ) {
*             return next( null, false );
*         }
*         next( null, true );
*     }
* }
*
* everyAsync( files, predicate, done );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var everyAsync = require( './every.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( everyAsync, 'factory', factory );


// EXPORTS //

module.exports = everyAsync;
