'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var everyByRight = require( './../lib' );

function isPositive( value ) {
	return ( value > 0 );
}

var bool;
var arr;
var i;

arr = new Array( 100 );
for ( i = 0; i < arr.length; i++ ) {
	arr[ i ] = randu();
}

bool = everyByRight( arr, isPositive );
console.log( bool );
