'use strict';

// MODULES //

var isCollection = require( '@stdlib/utils/is-collection' );
var isFunction = require( '@stdlib/utils/is-function' );


// MAIN //

/**
* Tests whether all elements in a collection pass a test implemented by a predicate function, iterating from right to left.
*
* ## Notes
*
* * For dynamic array resizing, the only behavior made intentionally consistent with `every` (iterating from left to right) is when elements are pushed onto the beginning (end) of an array. In other words, for `every()`, `[].push()` behavior is consistent with `everyRight()` `[].unshift()` behavior.
*
* @param {Collection} collection - input collection
* @param {Function} predicate - test function
* @param {*} [thisArg] - execution context
* @throws {TypeError} first argument must be a collection
* @throws {TypeError} second argument must be a function
* @returns {boolean} boolean indicating whether all elements pass a test
*
* @example
* function isPositive( v ) {
*     return ( v > 0 );
* }
*
* var arr = [ 1, 2, 3, 4 ];
*
* var bool = everyRight( arr, isPositive );
* // returns true
*/
function everyRight( collection, predicate, thisArg ) {
	var out;
	var len;
	var i;
	if ( !isCollection( collection ) ) {
		throw new TypeError( 'invalid input argument. First argument must be a collection. Value: `'+collection+'`.' );
	}
	if ( !isFunction( predicate ) ) {
		throw new TypeError( 'invalid input argument. Second argument must be a function. Value: `'+predicate+'`.' );
	}
	len = collection.length;
	for ( i = len-1; i >= 0; i-- ) {
		out = predicate.call( thisArg, collection[ i ], i, collection );
		if ( !out ) {
			return false;
		}
		// Account for dynamically resizing a collection...
		if ( len !== collection.length ) {
			i += ( collection.length - len );
			len = collection.length;
		}
	}
	return true;
} // end FUNCTION everyRight()


// EXPORTS //

module.exports = everyRight;
