'use strict';

/**
* Test whether all elements in a collection pass a test implemented by a predicate function, iterating from right to left.
*
* @module @stdlib/utils/every-right
*
* @example
* var everyRight = require( '@stdlib/utils/every-right' );
*
* function isPositive( v ) {
*     return ( v > 0 );
* }
*
* var arr = [ 1, 2, 3, 4 ];
*
* var bool = everyRight( arr, isPositive );
* // returns true
*/

// MODULES //

var everyRight = require( './every_right.js' );


// EXPORTS //

module.exports = everyRight;
