'use strict';

// MODULES //

var isCollection = require( '@stdlib/assert/is-collection' );


// MAIN //

/**
* Tests whether all elements in a collection are truthy.
*
* @param {Collection} collection - input collection
* @throws {TypeError} must provide a collection
* @returns {boolean} boolean indicating whether all elements are truthy
*
* @example
* var arr = [ 1, 1, 1, 1, 1 ];
*
* var bool = every( arr );
* // returns true
*/
function every( collection ) {
	var len;
	var i;
	if ( !isCollection( collection ) ) {
		throw new TypeError( 'invalid input argument. Must provide a collection. Value: `'+collection+'`.' );
	}
	len = collection.length;
	for ( i = 0; i < len; i++ ) {
		if ( !collection[ i ] ) {
			return false;
		}
	}
	return true;
} // end FUNCTION every()


// EXPORTS //

module.exports = every;
