'use strict';

var flattenArray = require( './../lib' );

var flatten;
var arr;
var out;

// Create a flatten function customized for flattening 3x3 arrays:
flatten = flattenArray.factory( [3, 3] );

arr = [
	[ 1, 2, 3 ],
	[ 4, 5, 6 ],
	[ 7, 8, 9 ]
];

out = flatten( arr );
console.log( out );
// => [ 1, 2, 3, 4, 5, 6, 7, 8, 9 ]
