/* eslint-disable object-curly-newline, object-curly-spacing */
'use strict';

var flattenArray = require( './../lib' );

var flatten;
var opts;
var arr;
var out;

opts = {
	'copy': true
};

flatten = flattenArray.factory( [3, 3], opts );

arr = [
	[ 1, 2, 3 ],
	[ 4, {'x': 5}, 6 ],
	[ 7, 8, 9 ]
];

out = flatten( arr );
console.log( out );
// => [ 1, 2, 3, 4, {'x':5}, 6, 7, 8, 9 ]

console.log( arr[1][1] === out[4] );
// => false
