'use strict';

// MODULES //

var tape = require( 'tape' );
var flattenArray = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof flattenArray, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a factory function', function test( t ) {
	t.equal( typeof flattenArray.factory, 'function', 'has a factory method' );
	t.end();
});
