'use strict';

/**
* Invoke a function once for each element in a collection.
*
* @module @stdlib/utils/for-each-async
*
* @example
* var readFile = require( '@stdlib/fs/read-file' );
* var forEachAsync = require( '@stdlib/utils/for-each-async' );
*
* var files = [
*     './beep.js',
*     './boop.js'
* ];
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( 'Successfully read all files.' );
* }
*
* function read( file, next ) {
*     var opts = {
*         'encoding': 'utf8'
*     };
*     readFile( file, opts, onFile );
*
*     function onFile( error ) {
*         if ( error ) {
*             error = new Error( 'unable to read file: '+file );
*             return next( error );
*         }
*         console.log( 'Successfuly read file: %s', file );
*         next();
*     }
* }
*
* forEachAsync( files, read, done );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var forEachAsync = require( './for_each.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( forEachAsync, 'factory', factory );


// EXPORTS //

module.exports = forEachAsync;
