'use strict';

var isEven = require( '@stdlib/assert/is-even' ).isPrimitive;
var forEach = require( './../lib' );

function log( value, index, collection ) {
	console.log( '%s: %d', index, value );
	if ( isEven( index ) ) {
		collection.shift();
	} else {
		collection.push( index+1 );
	}
}

var arr;
var i;

arr = new Array( 100 );
for ( i = 0; i < arr.length; i++ ) {
	arr[ i ] = i;
}

forEach( arr, log );
