'use strict';

var forIn = require( './../lib' );

function update( value, key, obj ) {
	console.log( '%s: %d', key, value );
	obj[ key ] *= value;
}

function Foo() {
	return this;
}

Foo.prototype.beep = 3.14;

var obj;
var key;
var i;

obj = new Foo();
for ( i = 0; i < 26; i++ ) {
	key = String.fromCharCode( 97 + i );
	obj[ key ] = i;
}

forIn( obj, update );
console.log( obj );
