'use strict';

var forOwn = require( './../lib' );

function update( value, key, obj ) {
	console.log( '%s: %d', key, value );
	obj[ key ] *= value;
}

var obj;
var key;
var i;

obj = {};
for ( i = 0; i < 26; i++ ) {
	key = String.fromCharCode( 97 + i );
	obj[ key ] = i;
}

forOwn( obj, update );
console.log( obj );
