'use strict';

// MODULES //

var getKeys = require( 'object-keys' ).shim();
var isFunction = require( '@stdlib/utils/is-function' );


// MAIN //

/**
* Invokes a function once for each own enumerable property of an object.
*
* ## Notes
*
* * Iteration order is __not__ guaranteed.
*
*
* @param {Object} obj - input object
* @param {Function} fcn - function to invoke
* @param {*} [thisArg] - execution context
* @throws {TypeError} first argument must be an object
* @throws {TypeError} second argument must be a function
* @returns {Object} obj - input object
*
* @example
* function log( v, key ) {
*     console.log( '%s: %d', key, v );
* }
*
* var obj = {
*     'a': 1,
*     'b': 2,
*     'c': 3,
*     'd': 4
* };
*
* forOwn( obj, log );
*/
function forOwn( obj, fcn, thisArg ) {
	var keys;
	var bool;
	var len;
	var k;
	var i;
	if ( typeof obj !== 'object' || obj === null ) {
		throw new TypeError( 'invalid input argument. First argument must be an object. Value: `'+obj+'`.' );
	}
	if ( !isFunction( fcn ) ) {
		throw new TypeError( 'invalid input argument. Second argument must be a function. Value: `'+fcn+'`.' );
	}
	keys = getKeys( obj );
	len = keys.length;
	for ( i = 0; i < len; i++ ) {
		k = keys[ i ];
		bool = fcn.call( thisArg, obj[ k ], k, obj );
		if ( bool === false ) {
			return obj;
		}
	}
	return obj;
} // end FUNCTION forOwn()


// EXPORTS //

module.exports = forOwn;
