'use strict';

/**
* Test whether a path exists on the filesystem.
*
* @module @stdlib/utils/fs/exists
*
* @example
* var exists = require( '@stdlib/utils/fs/exists' );
*
* // Sync:
* console.log( exists.sync( __dirname ) );
* // returns true
*
* console.log( exists.sync( 'beepboop' ) );
* // returns false
*
*
* // Async:
* exists( __dirname, done );
* exists( 'beepboop', done );
*
* function done( error, bool ) {
* 	if ( error ) {
* 		console.error( error.message );
* 	} else {
* 		console.log( bool );
* 	}
* }
*/

// EXPORTS //

// See the [archive]{@link https://github.com/nodejs/node-v0.x-archive/blob/d8baf8a2a4481940bfed0196308ae6189ca18eee/lib/fs.js#L222}.
module.exports = require( './async.js' );
module.exports.sync = require( './sync.js' );
