'use strict';

// MODULES //

var tape = require( 'tape' );
var fs = require( 'fs' );
var proxyquire = require( 'proxyquire' );
var exists = require( './../lib/async.js' );


// FIXME //

if ( typeof window !== 'undefined'  ) {
	// Don't run tests in the browser...for now...
	return;
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof exists, 'function', 'export is a function' );
	t.end();
});

tape( 'the function returns true if a path exists', function test( t ) {
	exists( __filename, onExists );
	function onExists( bool ) {
		t.equal( bool, true, 'returns true' );
		t.end();
	}
});

tape( 'the function returns false if a path does not exist', function test( t ) {
	exists( 'beepboopbebop', onExists );
	function onExists( bool ) {
		t.equal( bool, false, 'returns false' );
		t.end();
	}
});

tape( 'if `fs.access` is not supported, the function uses `fs.stat`', function test( t ) {
	var exists = proxyquire( './../lib/async.js', {
		'fs': {
			'stat': fs.stat,
			'access': void 0
		}
	});
	exists( __filename, onExists );
	function onExists( bool ) {
		t.equal( bool, true, 'returns true' );
		t.end();
	}
});

tape( 'if `fs.access` is not supported, the function uses `fs.stat`', function test( t ) {
	var exists = proxyquire( './../lib/async.js', {
		'fs': {
			'stat': fs.stat,
			'access': void 0
		}
	});
	exists( 'beepboopbebop', onExists );
	function onExists( bool ) {
		t.equal( bool, false, 'returns false' );
		t.end();
	}
});

tape( 'the function supports error-first callbacks (success)', function test( t ) {
	exists( __filename, onExists );
	function onExists( error, bool ) {
		t.equal( error, null, 'error is null' );
		t.equal( bool, true, 'returns true' );
		t.end();
	}
});

tape( 'the function supports error-first callbacks (failure)', function test( t ) {
	exists( 'beepboopbebop', onExists );
	function onExists( error, bool ) {
		t.ok( error, 'returns an error' );
		t.equal( bool, false, 'returns false' );
		t.end();
	}
});

tape( 'the function supports using `fs.stat` with error-first callbacks (success)', function test( t ) {
	var exists = proxyquire( './../lib/async.js', {
		'fs': {
			'stat': fs.stat,
			'access': void 0
		}
	});
	exists( __filename, onExists );
	function onExists( error, bool ) {
		t.equal( error, null, 'error is null' );
		t.equal( bool, true, 'returns true' );
		t.end();
	}
});

tape( 'the function supports using `fs.stat` with error-first callbacks (failure)', function test( t ) {
	var exists = proxyquire( './../lib/async.js', {
		'fs': {
			'stat': fs.stat,
			'access': void 0
		}
	});
	exists( 'beepboopbebop', onExists );
	function onExists( error, bool ) {
		t.ok( error, 'returns an error' );
		t.equal( bool, false, 'returns false' );
		t.end();
	}
});
